public class NewWays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// i // 0 1 2 3 4 5
		int[] a = { 1, 2, 3, 4, 5, 6 };

		System.out.println("Reverse Array : ");

		for (int i = a.length - 1; i >= 0; i--) {

			System.out.print(a[i] + " ");
		}

	}

}
