public class P2_DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Formula for calculate range -2^(n-1) to 2^((n-1)-1) Numerical without Decimal Data Types 

		byte a = 127; // Max Value
		byte b = -128; // Min Value

		short c = 32767; // Max Value
		short d = -32768; // Min Value

		int e = 2147483647;// Max Value
		int f = -2147483648;// Min Value

		long g = 9223372036854775807L; // Max Value
		long h = -9223372036854775808L;// Min Value

		float i = 2.24f;
		double j = 2.24;

		boolean k = true;
		boolean l = false;

		char m = 'a';
		char n = '1';
		char o = '@';

	}

}
